mapper.select:
select 

from ac_biz_lock
where tnt_inst_id = #{tntInstId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from ac_biz_lock
where tnt_inst_id = #{tntInstId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.insert:
insert into ac_biz_lock (tnt_inst_id, id, gmt_created, 
  gmt_modified, biz_id, biz_type, 
  lock_type, locked_time, master_lock_id
  )
values (#{tntInstId,jdbcType=VARCHAR}, #{id,jdbcType=VARCHAR}, #{gmtCreated,jdbcType=TIMESTAMP}, 
  #{gmtModified,jdbcType=TIMESTAMP}, #{bizId,jdbcType=VARCHAR}, #{bizType,jdbcType=VARCHAR}, 
  #{lockType,jdbcType=VARCHAR}, #{lockedTime,jdbcType=TIMESTAMP}, #{masterLockId,jdbcType=VARCHAR}
  )

mapper.update:
update ac_biz_lock
set gmt_created = #{gmtCreated,jdbcType=TIMESTAMP},
  gmt_modified = #{gmtModified,jdbcType=TIMESTAMP},
  biz_id = #{bizId,jdbcType=VARCHAR},
  biz_type = #{bizType,jdbcType=VARCHAR},
  lock_type = #{lockType,jdbcType=VARCHAR},
  locked_time = #{lockedTime,jdbcType=TIMESTAMP},
  master_lock_id = #{masterLockId,jdbcType=VARCHAR}
where tnt_inst_id = #{tntInstId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}
